def draw_lines(lines):
    '''the resized image size is (512, 512)

    
    '''
    blank_fig = np.zeros([512, 512])
    plt.imshow(blank_fig)

    for line in lines:
        plt.plot([line[0], line[2]], [line[1], line[3]], linewidth=1) 
        plt.scatter(line[0], line[1])
        plt.scatter(line[2], line[3])
    
    plt.show()


